import React from "react";
import { View, Text, TouchableOpacity, Image, ScrollView,Pressable } from "react-native";
import AntDesign from '@expo/vector-icons/AntDesign';
import { RootStackParamList } from "..";
import { useNavigation } from '@react-navigation/native';
import { useEveryData } from "../context/EveryDataContext";
import { Alert } from "react-native";



type WelcomePageNavigationProp = {
    navigate: (screen: keyof RootStackParamList) => void;
    goBack: () => void;
  };
export default function Mobile_Recharge2() {

  const {everydata, setEverydata} = useEveryData();
  const [operatortype, setoperatortype] = React.useState<string>('');
    const operators = [
        {
          id: 'gp',
          name: 'Grameenphone',
          logo: 'data:image/png;base64,'
        },
        {
          id: 'robi',
          name: 'Robi',
          logo: 'data:image/png;base64,'
        },
        {
          id: 'airtel',
          name: 'Airtel',
          logo: 'data:image/png;base64,'
        },
        {
          id: 'banglalink',
          name: 'Banglalink',
          logo: 'data:image/jpeg;base64,'
        },
        {
          id: 'teletalk',
          name: 'Teletalk',
          logo: 'data:image/png;base64,'
        }
      ];
      

    const navigation = useNavigation<WelcomePageNavigationProp>();

    const handleOperatorPress = (operator: any, everydata: any) => {
      const { phone } = everydata;
      const { name, logo } = operator;
    
      const cleanNumber = phone.replace(/[^0-9]/g, "").replace(/^88/, "");
      const prefix = cleanNumber.substring(0, 3);
    
      let detectedOperator = "";
    
      switch (prefix) {
        case "017":
        case "013":
          detectedOperator = "Grameenphone";
          break;
        case "018":
        case "016":
          detectedOperator = "Robi";
          break;
        case "014":
          detectedOperator = "Airtel";
          break;
        case "019":
          detectedOperator = "Banglalink";
          break;
        case "015":
          detectedOperator = "Teletalk";
          break;
      }
    
      setoperatortype(detectedOperator);
    
 
    
      if (name === detectedOperator) {
        setEverydata({ ...everydata, operator: name, logo });
        navigation.navigate("Mobile_Recharge3");
      } else {
        Alert.alert("Operator not found");
      }
    };
    

  return (

    
    <View className="flex-1 bg-white ">
      
      <View style={{ backgroundColor: '#f86c51', width: '100%', height: '16%', flexDirection: 'row', alignItems: 'center', gap: 7 }}>
      <Pressable onPress={() => navigation.goBack()}>
      <AntDesign name="arrow-left" size={24} color="white" style={{ marginLeft: 10 }} />
      </Pressable>
      <Text style={{ fontSize: 24, color: 'white', fontWeight: '600', flex: 1, textAlign: 'center' }}>Mobile Recharge</Text>
    </View>

      <ScrollView contentContainerStyle={{ flexGrow: 1, paddingBottom: 20 }}>
        
        <View className="items-center mt-10">
          <Image
            source={require('../../assets/images/Group 9.png')}
            className="w-28 h-28"
          />
        </View>

        
        <Text className="text-xl font-semibold text-center mt-6">
          Mobile Operator
        </Text>
        <Text className="text-gray-500 text-center mt-1 px-8">
          Select recipient's current mobile network operator
        </Text>

        
        <View className="mt-6 space-y-8 px-6">
          {operators.map((op) => (
            <Pressable
              key={op.id}
              className="flex flex-col mt-4 items-center border border-gray-400 justify-center  bg-white py-3 rounded-full"
              onPress={() => handleOperatorPress(op,everydata)}
              
            >
              <Image source= {{ uri: op?.logo }} className="w-40 h-20 resize-contain" />
            </Pressable>
          ))}
        </View>
      </ScrollView>
    </View>
  )
}
